// src/pages/Home.js
import React from 'react';
import { Link } from 'react-router-dom';
import './Home.css';

const Home = () => {
  return (
    <div className="home-container">
      <header className="home-header">
        <h1>MERN Task Manager</h1>
        <p>Stay organized, track your tasks, and boost productivity with a clean full-stack MERN application.</p>
        <div className="home-buttons">
          <Link to="/register" className="btn-primary">Get Started</Link>
          <Link to="/login" className="btn-secondary">Login</Link>
        </div>
        <p className="home-note">No credit card required. Just sign up and start managing your tasks.</p>
      </header>

      <section className="features">
        <div className="feature">
          <h3>Task Management</h3>
          <p>Create, update, and track tasks with a clean and simple interface.</p>
        </div>
        <div className="feature">
          <h3>Secure Auth</h3>
          <p>Authentication powered by Node.js, Express, and JWT for secure access.</p>
        </div>
        <div className="feature">
          <h3>MongoDB Backend</h3>
          <p>All your data is stored in MongoDB Atlas and served via a REST API.</p>
        </div>
      </section>
    </div>
  );
};

export default Home;
