// src/pages/Dashboard.js
import React, { useState, useEffect, useContext } from 'react';
import Navbar from '../components/Navbar';
import TaskForm from '../components/TaskForm';
import TaskList from '../components/TaskList';
import api from '../config/api';
import { AuthContext } from '../context/AuthContext';
import './Dashboard.css';

const Dashboard = () => {
  const { user } = useContext(AuthContext); // Get the current user
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showForm, setShowForm] = useState(false);
  const [editingTask, setEditingTask] = useState(null);

  useEffect(() => {
    if (user) fetchTasks();
    else setLoading(false); // no tasks if not logged in
  }, [user]);

  const fetchTasks = async () => {
    try {
      const token = localStorage.getItem('token'); // if you use token auth
      const response = await api.get('/tasks', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setTasks(response.data.data);
      setLoading(false);
    } catch (err) {
      setError('Failed to fetch tasks');
      setLoading(false);
    }
  };

  const handleAddTask = () => {
    setEditingTask(null);
    setShowForm(true);
  };

  const handleEditTask = (task) => {
    setEditingTask(task);
    setShowForm(true);
  };

  const handleDeleteTask = async (taskId) => {
    if (window.confirm('Are you sure you want to delete this task?')) {
      try {
        const token = localStorage.getItem('token');
        await api.delete(`/tasks/${taskId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setTasks(tasks.filter(task => task._id !== taskId));
      } catch (err) {
        setError('Failed to delete task');
      }
    }
  };

  const handleSaveTask = async (taskData) => {
    try {
      const token = localStorage.getItem('token');
      if (editingTask) {
        const response = await api.put(`/tasks/${editingTask._id}`, taskData, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setTasks(tasks.map(task => 
          task._id === editingTask._id ? response.data.data : task
        ));
      } else {
        const response = await api.post('/tasks', taskData, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setTasks([response.data.data, ...tasks]);
      }
      setShowForm(false);
      setEditingTask(null);
    } catch (err) {
      setError('Failed to save task');
    }
  };

  const handleCloseForm = () => {
    setShowForm(false);
    setEditingTask(null);
  };

  if (loading) {
    return (
      <div>
        <Navbar />
        <div className="loading">Loading tasks...</div>
      </div>
    );
  }

  return (
    <div>
      <Navbar />
      <div className="dashboard-container">
        <div className="dashboard-header">
          <h1>My Tasks</h1>
          <button className="add-task-btn" onClick={handleAddTask}>
            + Add Task
          </button>
        </div>

        {error && <div className="error-message">{error}</div>}

        {showForm && (
          <TaskForm
            task={editingTask}
            onSave={handleSaveTask}
            onClose={handleCloseForm}
          />
        )}

        <TaskList
          tasks={tasks}
          onEdit={handleEditTask}
          onDelete={handleDeleteTask}
        />

        {tasks.length === 0 && !showForm && (
          <div className="empty-state">
            <p>No tasks yet. Create your first task!</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
