import React from 'react';
import TaskItem from './TaskItem';
import './TaskList.css';

const TaskList = ({ tasks, onEdit, onDelete }) => {
  const tasksByStatus = {
    pending: tasks.filter(task => task.status === 'pending'),
    'in-progress': tasks.filter(task => task.status === 'in-progress'),
    completed: tasks.filter(task => task.status === 'completed')
  };

  return (
    <div className="task-list">
      <div className="task-column">
        <h3 className="column-title">Pending ({tasksByStatus.pending.length})</h3>
        <div className="task-items">
          {tasksByStatus.pending.map(task => (
            <TaskItem
              key={task._id}
              task={task}
              onEdit={onEdit}
              onDelete={onDelete}
            />
          ))}
        </div>
      </div>

      <div className="task-column">
        <h3 className="column-title">In Progress ({tasksByStatus['in-progress'].length})</h3>
        <div className="task-items">
          {tasksByStatus['in-progress'].map(task => (
            <TaskItem
              key={task._id}
              task={task}
              onEdit={onEdit}
              onDelete={onDelete}
            />
          ))}
        </div>
      </div>

      <div className="task-column">
        <h3 className="column-title">Completed ({tasksByStatus.completed.length})</h3>
        <div className="task-items">
          {tasksByStatus.completed.map(task => (
            <TaskItem
              key={task._id}
              task={task}
              onEdit={onEdit}
              onDelete={onDelete}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default TaskList;
