import React, { useState, useEffect, useContext } from 'react';
import Navbar from '../components/Navbar';
import TaskForm from '../components/TaskForm';
import TaskList from '../components/TaskList';
import api, { authApi } from '../config/api';
import { AuthContext } from '../context/AuthContext';
import './Dashboard.css';

const Dashboard = () => {
  const { user } = useContext(AuthContext); // âœ… Get token
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showForm, setShowForm] = useState(false);
  const [editingTask, setEditingTask] = useState(null);

  useEffect(() => {
    if (user?.token) fetchTasks();
  }, [user]);

  const fetchTasks = async () => {
    try {
      setLoading(true);
      const response = await authApi(user.token).get('/tasks'); // ðŸ”‘ Pass token
      setTasks(response.data.data);
      setLoading(false);
    } catch (err) {
      console.error(err);
      setError('Failed to fetch tasks (check login)');
      setLoading(false);
    }
  };

  const handleAddTask = () => {
    setEditingTask(null);
    setShowForm(true);
  };

  const handleEditTask = (task) => {
    setEditingTask(task);
    setShowForm(true);
  };

  const handleDeleteTask = async (taskId) => {
    if (window.confirm('Are you sure you want to delete this task?')) {
      try {
        await authApi(user.token).delete(`/tasks/${taskId}`);
        setTasks(tasks.filter(task => task._id !== taskId));
      } catch (err) {
        setError('Failed to delete task');
      }
    }
  };

  const handleSaveTask = async (taskData) => {
    try {
      if (editingTask) {
        const response = await authApi(user.token).put(
          `/tasks/${editingTask._id}`,
          taskData
        );
        setTasks(tasks.map(task => 
          task._id === editingTask._id ? response.data.data : task
        ));
      } else {
        const response = await authApi(user.token).post('/tasks', taskData);
        setTasks([response.data.data, ...tasks]);
      }
      setShowForm(false);
      setEditingTask(null);
    } catch (err) {
      setError('Failed to save task');
    }
  };

  const handleCloseForm = () => {
    setShowForm(false);
    setEditingTask(null);
  };

  if (!user) {
    return (
      <div>
        <Navbar />
        <p className="error-message">Please login to view tasks.</p>
      </div>
    );
  }

  if (loading) {
    return (
      <div>
        <Navbar />
        <div className="loading">Loading tasks...</div>
      </div>
    );
  }

  return (
    <div>
      <Navbar />
      <div className="dashboard-container">
        <div className="dashboard-header">
          <h1>My Tasks</h1>
          <button className="add-task-btn" onClick={handleAddTask}>
            + Add Task
          </button>
        </div>

        {error && <div className="error-message">{error}</div>}

        {showForm && (
          <TaskForm
            task={editingTask}
            onSave={handleSaveTask}
            onClose={handleCloseForm}
          />
        )}

        <TaskList
          tasks={tasks}
          onEdit={handleEditTask}
          onDelete={handleDeleteTask}
        />

        {tasks.length === 0 && !showForm && (
          <div className="empty-state">
            <p>No tasks yet. Create your first task!</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
