// src/pages/Home.js
import React from 'react';
import { useNavigate, Link } from 'react-router-dom';
import './Home.css';

const Home = () => {
  const navigate = useNavigate();

  const handleGetStarted = () => {
    navigate('/register');
  };

  return (
    <div className="home-page">
      <div className="home-inner">
        <section className="home-hero">
          <h1 className="home-title">Welcome to MERN Task Manager</h1>
          <p className="home-subtitle">
            Stay organized, track your tasks, and boost productivity with a simple, clean
            full-stack MERN application.
          </p>

          <div className="home-actions">
            <button className="primary-btn" onClick={handleGetStarted}>
              Get Started
            </button>
            <Link to="/login" className="secondary-btn">
              Login
            </Link>
          </div>

          <p className="home-caption">
            No credit card required. Just sign up and start managing your tasks.
          </p>
        </section>

        <section className="home-grid">
          <div className="home-card">
            <h3>Task Management</h3>
            <p>Create, update, and track tasks with a clean and simple interface.</p>
          </div>
          <div className="home-card">
            <h3>Secure Auth</h3>
            <p>Authentication powered by Node.js, Express, and JWT for secure access.</p>
          </div>
          <div className="home-card">
            <h3>MongoDB Backend</h3>
            <p>All your data is stored in MongoDB Atlas and served via a REST API.</p>
          </div>
        </section>
      </div>
    </div>
  );
};

export default Home;
